const horas=document.getElementById("horas"),milimetros=document.getElementById("milimetros"),metrosQuadrados=document.getElementById("metros-quadrados"),btnCalculate=document.getElementById("btn-calculate"),res=document.getElementById("res");function calculate(b){b.preventDefault(),"touchstart"===b.target&&b.preventDefault();let c=+horas.value,e=+milimetros.value,f=+metrosQuadrados.value,d=60*c,a=(e*f).toFixed(2),g=(a/1e3).toFixed(2),h=(a/c).toFixed(2),i=(a/d).toFixed(2),j=(a/(60*d)).toFixed(2);res.innerHTML=`Litros totais: ${a} litros / ${g} m\xb3<br>Litros por hora: ${h} litros<br>Litros por minuto: ${i} litros<br>Litros por segundo: ${j} litros`}btnCalculate.addEventListener("touchstart",calculate),btnCalculate.addEventListener("click",calculate)